import { FormEvent, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import Form from '../../components/form';
import { loginUser, useAuth } from '../../store/Auth/AuthContext';
import { LoginUser } from '../../type';

export default function LoginPage() {
    const [{ email, password }, setForm] = useState<LoginUser>({
        email: 'kiwi@gmail.com',
        password: 'kiwi123',
    });

    const {
        state: { isLoading, isAuthenticated },
        dispatch,
    } = useAuth();

    const navigate = useNavigate();

    const handleChange = (e: FormEvent) => {
        const target = e.target as HTMLInputElement;
        setForm((prevState: any) => {
            return { ...prevState, [target.name]: target.value };
        });
    };

    const handleSubmit = (e: FormEvent) => {
        e.preventDefault();

        loginUser();

        if (isAuthenticated) navigate('/');
    };

    return (
        <div className="flex justify-center items-center h-full w-full">
            <form
                className=" bg-white px-6 py-4 rounded-xl shadow-sm max-w-[420px] sm:w-[360px]"
                onSubmit={handleSubmit}
            >
                <header className="mb-6 text-center">
                    <h1 className="text-lg font-semibold text-cyan-600">
                        Let's Chat!
                    </h1>
                </header>
                <Form.FormInput
                    type="email"
                    name="email"
                    value={email}
                    placeholder="Email"
                    onChange={handleChange}
                />
                <Form.FormInput
                    type="password"
                    name="password"
                    value={password}
                    className="my-4"
                    placeholder="Password"
                    onChange={handleChange}
                />
                <Form.Button
                    title="Login"
                    className="w-full"
                    type="submit"
                    onSubmit={handleSubmit}
                    isLoading={isLoading}
                />
                <p className="text-sm mt-4">
                    Have an account ?{' '}
                    <Link
                        to="/signup"
                        className=" text-cyan-500 hover:text-cyan-700 hover:underline"
                    >
                        Sign up
                    </Link>
                </p>
            </form>
        </div>
    );
}
