import { authClient } from '../../api';
import { RegisterUser, LoginUser } from '../../type';
import { Dispatch } from './AuthContext';

async function registerUser(dispatch: Dispatch, inputs: RegisterUser) {
    dispatch({ type: 'REGISTER_PENDING' });
    try {
        const res = await authClient.registerUser(inputs);
        dispatch({ type: 'REGISTER_SUCCESS', payload: res });
    } catch (err) {
        console.error(err);
        dispatch({ type: 'REGISTER_FAILED', payload: err as string });
    }
}

async function loginUser(dispatch: Dispatch, inputs: LoginUser) {
    dispatch({ type: 'LOGIN_PENDING' });

    try {
        const res = await authClient.loginUser(inputs);
        console.log(res);
        dispatch({ type: 'LOGIN_SUCCESS', payload: res });
    } catch (err) {
        dispatch({ type: 'LOGIN_FAILED', payload: err as string });
    }
}

export { registerUser, loginUser };
