import { FormEvent } from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';

import Form from '../../components/form';
import { registerUser } from '../../store/Auth/AuthActions';
import { useAuth } from '../../store/Auth/AuthContext';
import { RegisterUser } from '../../type';

export default function SignupPage() {
    const [{ username, email, password }, setForm] = useState<RegisterUser>({
        username: 'kiwi',
        password: 'kiwi',
        email: 'kiwi@gmail.com',
    });

    const { state, dispatch } = useAuth() as { state: any; dispatch: any };

    const handleChange = (e: FormEvent<HTMLInputElement>) => {
        const target = e.target as HTMLInputElement;
        setForm((prevState: any) => {
            return { ...prevState, [target.name]: target.value };
        });
    };

    const handleSubmit = (e: FormEvent) => {
        e.preventDefault();
        registerUser(dispatch, { username, email, password });
    };

    return (
        <div className="flex justify-center items-center w-full h-full">
            <form
                className=" bg-white px-6 py-4 rounded-xl shadow-sm max-w-[420px] sm:w-[360px]"
                onSubmit={handleSubmit}
            >
                <header className="mb-6 text-center">
                    <h1 className="text-lg font-semibold text-cyan-600">
                        Let's Chat!
                    </h1>
                </header>
                <Form.FormInput
                    className="mb-4"
                    name="username"
                    value={username}
                    placeholder="Username"
                    onChange={handleChange}
                    required
                />
                <Form.FormInput
                    type="email"
                    name="email"
                    value={email}
                    placeholder="Email"
                    onChange={handleChange}
                    required
                />
                <Form.FormInput
                    type="password"
                    name="password"
                    value={password}
                    className="my-4"
                    placeholder="Password"
                    onChange={handleChange}
                    required
                />
                <Form.Button title="Sign up" className="w-full" type="submit" />
                <p className="text-sm mt-4">
                    Have an account ?{' '}
                    <Link
                        to="/login"
                        className=" text-cyan-500 hover:text-cyan-700 hover:underline"
                    >
                        Login
                    </Link>
                </p>
            </form>
        </div>
    );
}
