import React, { FormEvent } from 'react';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { InputType } from 'zlib';

import Form from '../../components/form';
import { RegisterUser } from '../../type';

export default function SignupPage() {
    const [form, setForm] = useState<RegisterUser>();

    console.log(form);

    const handleChange = (e: FormEvent<HTMLInputElement>) => {
        const target = e.target as HTMLInputElement;
        setForm((prevState: any) => {
            return { ...prevState, [target.name]: target.value };
        });
    };

    const handleSubmit = (e: FormEvent) => {
        e.preventDefault();
    };

    return (
        <div className="flex justify-center items-center w-full h-full">
            <form
                className=" bg-white px-6 py-4 rounded-xl shadow-sm max-w-[420px] sm:w-[360px]"
                onSubmit={handleSubmit}
            >
                <header className="mb-6 text-center">
                    <h1 className="text-lg font-semibold text-cyan-600">
                        Let's Chat!
                    </h1>
                </header>
                <Form.FormInput
                    className="mb-4"
                    name="username"
                    placeholder="Username"
                    onChange={handleChange}
                />
                <Form.FormInput
                    type="email"
                    name=""
                    placeholder="Email"
                    onChange={handleChange}
                />
                <Form.FormInput
                    type="password"
                    className="my-4"
                    placeholder="Password"
                    onChange={handleChange}
                />
                <Form.Button title="Sign up" className="w-full" type="submit" />
                <p className="text-sm mt-4">
                    Have an account ?{' '}
                    <Link to="/login" className="underline text-cyan-500">
                        Login
                    </Link>
                </p>
            </form>
        </div>
    );
}
